import './app.css'
import User from './component/User/user'
import CardMini from './component/cardMini/CardMini'
import data from './data.json'
import './component/cardMini/CardMini.css'
import {useState} from 'react'

import img from '../src/component/User/user.png'
import '../src/component/User/user.css'

function App() {

  const [monState, setMonState] = useState("daily")

  const daily = () => {
    setMonState("daily")
  }

  const weekly = () => {
    setMonState("weekly")
  }

  const monthly = () => {
    setMonState("monthly")
  }

  return (
    <div className="App">
      <div className="allCards">
        <User />

        <div className="colonne1">
        <CardMini titre={data[0].title} heure={data[0].timeframes[monState].current} heure2={data[0].timeframes[monState].previous} bgcolor={"bg-light"} svg={0} hover={"nbrHeuresWork"} />
        <CardMini titre={data[3].title} heure={data[3].timeframes[monState].current} heure2={data[3].timeframes[monState].previous} bgcolor={"bg-limeGreen"} svg={1} hover={"nbrHeuresExercices"} />
        </div>
        <div className="colonne2">
        <CardMini titre={data[1].title} heure={data[1].timeframes[monState].current} heure2={data[1].timeframes[monState].previous} bgcolor={"bg-softBlue"} svg={2} hover={"nbrHeuresPlay"} />
          <CardMini titre={data[4].title} heure={data[4].timeframes[monState].current} heure2={data[4].timeframes[monState].previous} bgcolor={"bg-violet"} svg={3} hover={"nbrHeuresSocial"} />
        </div>
        <div className="colonne3">
        <CardMini titre={data[2].title} heure={data[2].timeframes[monState].current} heure2={data[2].timeframes[monState].previous} bgcolor={"bg-lightRed"} svg={4} hover={"nbrHeuresStudy"} />
          <CardMini titre={data[5].title} heure={data[5].timeframes.daily.current} heure2={data[5].timeframes.daily.previous} bgcolor={"bg-softOrange"} svg={5} hover={"nbrHeuresSelf"} />
        </div>
      </div>
    </div>
  );
}

export default App;
