import './app.css'
import User from './component/User/user'
import CardMini from './component/cardMini/CardMini'
import data from './data.json'
import './component/cardMini/CardMini.css'
import {useState} from 'react'

function App() {

  const [monState, setMonState] = useState("weekly")

  const daily = () => {
    setMonState("daily")
  }

  const weekly = () => {
    setMonState("weekly")
  }

  const monthly = () => {
    setMonState("monthly")
  }

  return (
    <div className="App">

      {data.map((data) => (<CardMini titre={data.title} heure={data.timeframes[monState].current} heure2={data.timeframes[monState].previous} bgcolor={"bg-light"} svg={0} hover={"nbrHeuresWork"} />))}

      <div className="allCards">
        <User daily={daily} weekly={weekly} monthly={monthly}/>

        <div className="colonne1">

        </div>
        <div className="colonne2">

        </div>
        <div className="colonne3">

        </div>
      </div>
    </div>
  );
}

export default App;
